countOccurences :: String -> Char -> Int
countOccurences str c = length $ filter (== c) str

countLeftPar :: String -> Int
countLeftPar str = countOccurences str '('

countRightPar :: String -> Int
countRightPar str = countOccurences str ')'

santasFloor :: String -> Int
santasFloor str = (countLeftPar str) - (countRightPar str)

test1 = santasFloor "(())" == 0
test2 = santasFloor "()()" == 0
test3 = santasFloor "(((" == 3
test4 = santasFloor "(()(()(" == 3
test5 = santasFloor "))(((((" == 3
test6 = santasFloor "())" == -1
test7 = santasFloor "))(" == -1
test8 = santasFloor ")))" == -3
test9 = santasFloor ")())())" == -3
testall = test1 && test2 && test3 && test4 && test5 && test6 && test7 && test8 && test9

